import React from 'react';
import Form from './components/Form';
import Card from './components/Card';
import Cardcreate from './components/Cardcreate';

class App extends React.Component {
  constructor() {
    super();
    this.stateInitial = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
    };
    this.state = {
      ...this.stateInitial,
      cards: [],
      hasTrunfo: false,
      cardsFilterName: '',
    };
  }

  isFormValidate = () => {
    const {
      cardName,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardDescription,
    } = this.state;
    const array = [
      cardName,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardDescription,
    ];
    if (this.validadeAtribute(+cardAttr1, +cardAttr2, +cardAttr3)) return false;
    return !array.some((index) => index === '');
  };

  onInputChange = ({ target }) => {
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [target.name]: value,
    });
  };

  onSaveButtonClick = (event) => {
    event.preventDefault();
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    } = this.state;

    const cardFinishi = {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    };
    if (cardTrunfo) {
      this.setState({ hasTrunfo: true });
    }
    this.setState(({ cards }) => ({
      cards: [...cards, cardFinishi],
    }));
    this.setState(this.stateInitial);
  };

  onRemoveCard = (cardname, isTrunfo) => {
    const { cards } = this.state;
    const cardsFilter = cards.filter((card) => card.cardName !== cardname);
    if (isTrunfo) {
      this.setState({
        hasTrunfo: false,
        cards: cardsFilter,
      });
    } else {
      this.setState({
        cards: cardsFilter,
      });
    }
  };

  createdCardFilter = () => {
    const { cards, cardsFilterName } = this.state;
    cards.filter(({ cardName }) => console.log(cards, cardName));
  };

  cardsFinishFilter = ({ target: { value } }) => {
    this.setState({ cardsFilterName: value });
  };

  validadeAtribute(...arr) {
    if (arr.some((number) => number > +'90' || number < 0)) return true;
    if (arr.reduce((acc, number) => acc + number) > +'210') return true;
    return false;
  }

  render() {
   this.createdCardFilter()
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      hasTrunfo,
      cards,
      cardsFilterName,
    } = this.state;
    const { onSaveButtonClick, onInputChange } = this;
    return (
      <>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ !this.isFormValidate() }
          onInputChange={ onInputChange }
          onSaveButtonClick={ onSaveButtonClick }
        />
        <div className="Preview">
          <Card
            cardName={ cardName }
            cardImage={ cardImage }
            cardDescription={ cardDescription }
            cardAttr1={ cardAttr1 }
            cardAttr2={ cardAttr2 }
            cardAttr3={ cardAttr3 }
            cardRare={ cardRare }
            cardTrunfo={ cardTrunfo }
          />
        </div>
        <div className="card">
          <input
            type="text"
            data-testid="name-filter"
            onChange={ this.cardsFinishFilter }
            value={ cardsFilterName }
          />
          {cards.length
            && cards.map((cardatual) => (
              <Cardcreate
                key={ cardatual.cardName }
                obj={ cardatual }
                onRemoveCard={ this.onRemoveCard }
              />
            ))}
        </div>
      </>
    );
  }
}

export default App;
