import React from "react";
import Form from "./components/Form";
import Card from "./components/Card";

class App extends React.Component {
  constructor() {
    super();
    const stateInitial = {
      cardName: "",
      cardDescription: "",
      cardAttr1: "",
      cardAttr2: "",
      cardAttr3: "",
      cardImage: "",
      cardRare: "",
      cardTrunfo: false,
      hasTrunfo: false,
    };
    this.state = stateInitial;
  }

  isFormValidate = () => {
    const { cardName, cardAttr1, cardAttr2, cardAttr3, cardImage, cardRare } =
      this.state;
    const array = [
      cardName,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
    ];
    if (this.validadeAtribute(+cardAttr1, +cardAttr2, +cardAttr3)) return false;
    return array.every((index) => index !== "");
  };

  onInputChange = ({ target }) => {
    const value = target.type === "checkbox" ? target.checked : target.value;
    this.setState({
      [target.name]: value,
    });
  };

  onSaveButtonClick = () => {};

  validadeAtribute(...arr) {
    if (arr.some((number) => number > +"90" || number < 0)) console.log("oi");
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      hasTrunfo,
    } = this.state;
    const { onSaveButtonClick, onInputChange } = this;
    return (
      <>
        <Form
          cardName={cardName}
          cardDescription={cardDescription}
          cardAttr1={cardAttr1}
          cardAttr2={cardAttr2}
          cardAttr3={cardAttr3}
          cardImage={cardImage}
          cardRare={cardRare}
          cardTrunfo={cardTrunfo}
          hasTrunfo={hasTrunfo}
          isSaveButtonDisabled={!this.isFormValidate()}
          onInputChange={onInputChange}
          onSaveButtonClick={onSaveButtonClick}
        />
        <div className='cards'>
          <Card
            cardName={cardName}
            cardImage={cardImage}
            cardDescription={cardDescription}
            cardAttr1={cardAttr1}
            cardAttr2={cardAttr2}
            cardAttr3={cardAttr3}
            cardRare={cardRare}
            cardTrunfo={cardTrunfo}
          />
        </div>
      </>
    );
  }
}

export default App;
