import { element } from "prop-types";
import React from "react";
import Form from "./components/Form";

class App extends React.Component {
  constructor() {
    super();
    const stateInitial = {
      cardName: "",
      cardDescription: "",
      cardAttr1: "",
      cardAttr2: "",
      cardAttr3: "",
      cardImage: "",
      cardRare: "",
      cardTrunfo: false,
      hasTrunfo: false,
      isSaveButtonDisabled: true,
    };
    this.state = stateInitial;
  }

  onValidationState = () => {
    console.log("entrou");
    const { cardName, cardAttr1, cardAttr2, cardAttr3, cardImage, cardRare } =
      this.state;
    const array = [
      cardName,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
    ];
    console.log(array.some((element) => element === ""));
  };

  onInputChange = ({ target }) => {
    const value = target.type === "checkbox" ? target.checked : target.value;
    this.setState({
      [target.name]: value,
    });
    this.onValidationState();
  };

  onSaveButtonClick = () => {};

  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      hasTrunfo,
      isSaveButtonDisabled,
    } = this.state;
    const { onSaveButtonClick, onInputChange } = this;
    return (
      <Form
        cardName={cardName}
        cardDescription={cardDescription}
        cardAttr1={cardAttr1}
        cardAttr2={cardAttr2}
        cardAttr3={cardAttr3}
        cardImage={cardImage}
        cardRare={cardRare}
        cardTrunfo={cardTrunfo}
        hasTrunfo={hasTrunfo}
        isSaveButtonDisabled={isSaveButtonDisabled}
        onInputChange={onInputChange}
        onSaveButtonClick={onSaveButtonClick}
      />
    );
  }
}

export default App;
