import React from 'react';
import Form from './components/Form';
import Card from './components/Card';

class App extends React.Component {
  constructor() {
    super();
    const stateInitial = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardImage: '0',
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: false,
    };
    this.state = stateInitial;
  }

  isFormValidate = () => {
    const { cardName,
      cardAttr1, cardAttr2,
      cardAttr3, cardImage,
      cardRare,
      cardDescription } = this.state;
    const array = [
      cardName,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardDescription,
    ];
    if (this.validadeAtribute(+cardAttr1, +cardAttr2, +cardAttr3)) return false;
    return !array.some((index) => index === '');
  };

  onInputChange = ({ target }) => {
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [target.name]: value,
    });
  };

  onSaveButtonClick = () => {};

  validadeAtribute(...arr) {
    if (arr.some((number) => number > +'90' || number < 0)) return true;
    if (arr.reduce((acc, number) => acc + number) > +'210') return true;
    return false;
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      hasTrunfo,
    } = this.state;
    const { onSaveButtonClick, onInputChange } = this;
    return (
      <>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ !this.isFormValidate() }
          onInputChange={ onInputChange }
          onSaveButtonClick={ onSaveButtonClick }
        />
        <div className="cards">
          <Card
            cardName={ cardName }
            cardImage={ cardImage }
            cardDescription={ cardDescription }
            cardAttr1={ cardAttr1 }
            cardAttr2={ cardAttr2 }
            cardAttr3={ cardAttr3 }
            cardRare={ cardRare }
            cardTrunfo={ cardTrunfo }
          />
        </div>
      </>
    );
  }
}

export default App;
