import React, { Component } from "react";
import PropTypes from "prop-types";

export default class Card extends Component {
  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    } = this.props;
    return (
      <div data-testid='name-card'>
        {cardName}
        <img src={cardImage} data-testid='image-card' alt={cardName} />
        <p data-testid='description-card'>{cardDescription}</p>
        <p data-testid='attr1-card'>Atribuo 1 {cardAttr1}</p>
        <p data-testid='attr2-card'>Atribuo 2 {cardAttr2}</p>
        <p data-testid='attr3-card'>Atribuo 3 {cardAttr3}</p>
        <span data-testid='rare-card'>{cardRare}</span>
        {cardTrunfo && <span data-testid='trunfo-card'>Super Trunfo</span>}
      </div>
    );
  }
}

Card.protoTypes = {
  cardName: <protoTypes className="string isRequired"></protoTypes>,
  cardDescription: <protoTypes className="string isRequired"></protoTypes>,
  cardAttr1: <protoTypes className="string isRequired"></protoTypes>,
  cardAttr2: <protoTypes className="string isRequired"></protoTypes>,
  cardAttr3: <protoTypes className="string isRequired"></protoTypes>,
  cardImage: <protoTypes className="string isRequired"></protoTypes>,
  cardRare: <protoTypes className="string isRequired"></protoTypes>,
  cardTrunfo: <protoTypes className="string isRequired"></protoTypes>,
};
